import React, { useState } from 'react';
import axios from 'axios';
import { useLocation, Link, useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';

function Register() {
  const location = useLocation();
  const isDistributor = location.pathname === '/register/distributor';
  const navigate = useNavigate();

  console.log('Current Path:', location.pathname); // Debug: Log the current path
  console.log('Is Distributor:', isDistributor); // Debug: Log the value of isDistributor

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: isDistributor ? 'agent' : 'user', // Default role based on the registration type
  });

  console.log('Form Data:', formData); // Debug: Log the form data

  const [error, setError] = useState('');
  const [acceptTerms, setAcceptTerms] = useState(false);

  const termsAndConditions = `
1. Account Terms
   - You must be 18 years or older
   - You must provide valid identification
   - You are responsible for maintaining account security

2. Investment Terms
   - All investments carry risk
   - Past performance is not indicative of future results
   - Minimum investment amounts apply

3. Service Terms
   - We reserve the right to modify services
   - Service availability may vary
   - Terms may be updated periodically
`;

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (formData.password !== formData.confirmPassword) {
      toast.error('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('http://localhost:5000/auth/signup', formData);
      console.log('Signup successful:', response.data);
      setError('');
      toast.success('Registration successful! Please login.');
      
      // Redirect to login page after a short delay
      setTimeout(() => {
        navigate('/login');
      }, 1000);

    } catch (err) {
      console.error('Signup error:', err.response?.data || err.message);
      setError(err.response?.data?.error || 'An error occurred during signup.');
      toast.error(err.response?.data?.message || 'Registration failed');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="container">
      <div className="formWrapper">
        <h2 className="title">Register {isDistributor ? 'as Distributor' : ''}</h2>

        {isDistributor && (
          <div style={{ backgroundColor: '#f8f9fa', padding: '20px', borderRadius: '5px', marginBottom: '20px' }}>
            <h2>Why Join as a Distributor?</h2>
            <ul>
              <li>Work from home and earn money.</li>
              <li>Flexible working hours.</li>
              <li>Access to exclusive training and resources.</li>
              <li>Be part of a growing community.</li>
            </ul>
          </div>
        )}

        {error && <div style={{ color: 'red', marginBottom: '20px' }}>{error}</div>}

        <form className="form" onSubmit={handleSubmit}>
          <div className="inputGroup">
            <label className="label" htmlFor="name">Name</label>
            <input
              type="text"
              id="name"
              className="input"
              placeholder="Enter your name"
              value={formData.name}
              onChange={handleChange}
              name="name"
              required
            />
          </div>
          <div className="inputGroup">
            <label className="label" htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              className="input"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleChange}
              name="email"
              required
            />
          </div>
          <div className="inputGroup">
            <label className="label" htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              className="input"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleChange}
              name="password"
              required
            />
          </div>
          <div className="inputGroup">
            <label className="label" htmlFor="confirmPassword">Confirm Password</label>
            <input
              type="password"
              id="confirmPassword"
              className="input"
              placeholder="Enter your password again"
              value={formData.confirmPassword}
              onChange={handleChange}
              name="confirmPassword"
              required
            />
          </div>
          {!isDistributor && (
            <div className="inputGroup">
              <label className="label" htmlFor="role">Role</label>
              <select
                id="role"
                className="input"
                value={formData.role}
                onChange={handleChange}
                name="role"
              >
                <option value="user">User</option>
              </select>
            </div>
          )}
          {isDistributor && (
            <input
              type="hidden"
              name="role"
              value="agent" // Force the role to be "agent" for distributor registration
            />
          )}
          <div className="terms-section">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={acceptTerms}
                onChange={(e) => setAcceptTerms(e.target.checked)}
                required
              />
              I accept the terms and conditions
            </label>
            <div className="terms-content">
              <pre>{termsAndConditions}</pre>
            </div>
          </div>
          <button type="submit" className="button">Register</button>
        </form>
        <p className="linkText">
          Already have an account? <Link to="/login" className="link">Login</Link>
        </p>
      </div>
    </div>
  );
}

export default Register;
