// AgentDashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import jwtDecode from 'jwt-decode';

const AgentDashboard = () => {
  const [agentData, setAgentData] = useState(null);
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (token) {
      const decoded = jwtDecode(token);
      const { agentId } = decoded;

      if (!agentId) {
        console.error('No agentId found in user object:', decoded);
        return;
      }

      // Fetch agent data using the agentId
      axios
        .get(`/api/agents/${agentId}`, {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setAgentData(response.data);
        })
        .catch((error) => {
          console.error('Error fetching agent data:', error);
        });
    }
  }, [token]);

  return (
    <div>
      <h1>Agent Dashboard</h1>
      {agentData ? (
        <div>
          <p>Name: {agentData.name}</p>
          <p>Leads Generated: {agentData.leadsGenerated}</p>
          <p>Monthly Payout: {agentData.monthlyPayout}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default AgentDashboard;
