import React, { useState, useEffect } from 'react';
import axios from 'axios';

function AgentDashboard() {
  const [agentData, setAgentData] = useState(null);
  const [newLead, setNewLead] = useState({
    clientName: '',
    clientContact: '',
    leadValue: '',
  });
  const [error, setError] = useState('');

  // Fetch agent data
  useEffect(() => {
    const fetchAgentData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/agents/A001'); // Replace A001 with dynamic agent ID
        setAgentData(response.data);
      } catch (err) {
        setError('Failed to fetch dashboard data.');
      }
    };
    fetchAgentData();
  }, []);

  // Handle lead submission
  const handleSubmitLead = async () => {
    try {
      const response = await axios.post('http://localhost:5000/agents/A001/submit-lead', newLead);
      setAgentData(response.data.agent); // Update agent data with the new lead
      setNewLead({ clientName: '', clientContact: '', leadValue: '' }); // Reset form
      setError('');
    } catch (err) {
      setError('Failed to submit lead.');
    }
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Agent Dashboard</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {agentData ? (
        <>
          <h2>Welcome, {agentData.name}</h2>
          <p>
            <strong>Monthly Payout:</strong> ₹{agentData.monthlyPayout.toFixed(2)}
          </p>

          <h3>Your Leads</h3>
          <ul>
            {agentData.clients.length > 0 ? (
              agentData.clients.map((client, index) => (
                <li key={index}>
                  {client.name} - {client.contact} - ₹{client.leadValue}
                </li>
              ))
            ) : (
              <p>No leads submitted yet.</p>
            )}
          </ul>

          <h3>Submit a New Lead</h3>
          <div>
            <input
              type="text"
              placeholder="Client Name"
              value={newLead.clientName}
              onChange={(e) => setNewLead({ ...newLead, clientName: e.target.value })}
              style={{ padding: '10px', marginRight: '10px', width: '300px' }}
            />
            <input
              type="text"
              placeholder="Client Contact"
              value={newLead.clientContact}
              onChange={(e) => setNewLead({ ...newLead, clientContact: e.target.value })}
              style={{ padding: '10px', marginRight: '10px', width: '300px' }}
            />
            <input
              type="number"
              placeholder="Lead Value (₹)"
              value={newLead.leadValue}
              onChange={(e) => setNewLead({ ...newLead, leadValue: e.target.value })}
              style={{ padding: '10px', marginRight: '10px', width: '300px' }}
            />
            <button
              onClick={handleSubmitLead}
              style={{
                padding: '10px 20px',
                borderRadius: '5px',
                border: 'none',
                backgroundColor: '#007BFF',
                color: 'white',
                cursor: 'pointer',
              }}
            >
              Submit Lead
            </button>
          </div>
        </>
      ) : (
        <p>Loading dashboard...</p>
      )}
    </div>
  );
}

export default AgentDashboard;
