import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

// Register chart components
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function MutualFundData() {
  const [symbol, setSymbol] = useState('VFIAX'); // Default symbol
  const [data, setData] = useState(null);
  const [error, setError] = useState('');
  const [chartData, setChartData] = useState(null);

  // Fetch data from the backend
  const fetchData = async (fundSymbol) => {
    try {
      const response = await axios.get(`http://localhost:5000/mutualfunds/${fundSymbol}`);
      setData(response.data);

      // Process data for the graph
      if (response.data && response.data.quoteResponse) {
        const quotes = response.data.quoteResponse.result;
        const dates = quotes.map((quote) => quote.regularMarketTime);
        const closingPrices = quotes.map((quote) => quote.regularMarketPrice);

        setChartData({
          labels: dates,
          datasets: [
            {
              label: `${fundSymbol} Market Price`,
              data: closingPrices,
              borderColor: 'rgba(75, 192, 192, 1)',
              backgroundColor: 'rgba(75, 192, 192, 0.2)',
              fill: true,
            },
          ],
        });
      }
      setError('');
    } catch (err) {
      setError('Error fetching mutual fund data. Please try again.');
    }
  };

  // Fetch data on component load
  useEffect(() => {
    fetchData(symbol);
  }, []); // Run once on mount

  // Handle symbol change
  const handleFetchData = () => {
    if (!symbol) {
      setError('Please enter a mutual fund symbol.');
      return;
    }
    fetchData(symbol);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Mutual Fund Data Viewer</h1>
      <div>
        <input
          type="text"
          placeholder="Enter Mutual Fund Symbol"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value)}
          style={{
            padding: '10px',
            width: '300px',
            marginRight: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
          }}
        />
        <button
          onClick={handleFetchData}
          style={{
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            backgroundColor: '#007BFF',
            color: 'white',
            cursor: 'pointer',
          }}
        >
          Fetch Data
        </button>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Render Chart */}
      {chartData ? (
        <div style={{ marginTop: '20px' }}>
          <Line
            data={chartData}
            options={{
              responsive: true,
              plugins: {
                legend: {
                  position: 'top',
                },
                title: {
                  display: true,
                  text: `Market Prices for ${symbol}`,
                },
              },
            }}
          />
        </div>
      ) : (
        <p>No chart data available.</p>
      )}
    </div>
  );
}

export default MutualFundData;
