import React, { useState } from 'react';
import axios from 'axios';

function StockData() {
    const [symbol, setSymbol] = useState('');
    const [data, setData] = useState(null);
    const [availableStocks, setAvailableStocks] = useState([]);
    const [error, setError] = useState('');

    // Function to fetch stock data from the backend
    const fetchData = async (stockSymbol) => {
        try {
            const response = await axios.get(`http://localhost:5000/stock/${stockSymbol}`);
            setData(response.data);
            setError('');
        } catch (err) {
            setError('Failed to fetch data. Please try again.');
            console.error('Error:', err.message);
        }
    };

    // Function to fetch the list of available traded stocks
    const fetchAvailableStocks = async () => {
        try {
            const response = await axios.get('http://localhost:5000/available-traded');
            setAvailableStocks(response.data);
            setError('');
        } catch (err) {
            setError('Failed to fetch available stocks. Please try again.');
            console.error('Error:', err.message);
        }
    };

    // Handle the search
    const handleSearch = () => {
        if (!symbol) {
            setError('Please enter a stock symbol.');
            return;
        }
        fetchData(symbol);
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>Stock Data Viewer</h1>
            <div>
                <input
                    type="text"
                    placeholder="Enter Stock Symbol (e.g., AAPL)"
                    value={symbol}
                    onChange={(e) => setSymbol(e.target.value)}
                    style={{
                        padding: '10px',
                        marginRight: '10px',
                        width: '300px',
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                    }}
                />
                <button
                    onClick={handleSearch}
                    style={{
                        padding: '10px 20px',
                        borderRadius: '5px',
                        border: 'none',
                        backgroundColor: '#007BFF',
                        color: 'white',
                        cursor: 'pointer',
                    }}
                >
                    Search
                </button>
            </div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {data && (
                <div style={{ marginTop: '20px' }}>
                    <h2>Stock Data for {symbol.toUpperCase()}</h2>
                    <p><strong>Current Price:</strong> ${data.price}</p>
                    <p><strong>High Price:</strong> ${data.dayHigh}</p>
                    <p><strong>Low Price:</strong> ${data.dayLow}</p>
                    <p><strong>Previous Close:</strong> ${data.previousClose}</p>
                </div>
            )}

            <div style={{ marginTop: '40px' }}>
                <button
                    onClick={fetchAvailableStocks}
                    style={{
                        padding: '10px 20px',
                        borderRadius: '5px',
                        border: 'none',
                        backgroundColor: '#28a745',
                        color: 'white',
                        cursor: 'pointer',
                    }}
                >
                    Fetch Available Traded Stocks
                </button>
                {availableStocks.length > 0 && (
                    <div style={{ marginTop: '20px' }}>
                        <h2>Available Traded Stocks</h2>
                        <ul>
                            {availableStocks.map((stock, index) => (
                                <li key={index}>
                                    {stock.symbol} - {stock.name}
                                </li>
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default StockData;
