import React, { useState, useEffect } from 'react';
import axios from 'axios';

function MutualFundData() {
  const [symbol, setSymbol] = useState('VFIAX'); // Default symbol to fetch initially
  const [data, setData] = useState(null);
  const [error, setError] = useState('');

  // Function to fetch mutual fund data
  const fetchData = async (fundSymbol) => {
    try {
      const response = await axios.get(`http://localhost:5000/mutualfunds/${fundSymbol}`);
      setData(response.data);
      setError('');
    } catch (err) {
      setError('Error fetching mutual fund data. Please try again.');
    }
  };

  // Fetch data when the component loads
  useEffect(() => {
    fetchData(symbol);
  }, []); // Empty dependency array ensures it runs once when the component mounts

  // Handle symbol change and fetch data
  const handleFetchData = () => {
    if (!symbol) {
      setError('Please enter a mutual fund symbol.');
      return;
    }
    fetchData(symbol);
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1>Mutual Fund Data Fetcher</h1>
      <div>
        <input
          type="text"
          placeholder="Enter Mutual Fund Symbol"
          value={symbol}
          onChange={(e) => setSymbol(e.target.value)}
          style={{
            padding: '10px',
            width: '300px',
            marginRight: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
          }}
        />
        <button
          onClick={handleFetchData}
          style={{
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            backgroundColor: '#007BFF',
            color: 'white',
            cursor: 'pointer',
          }}
        >
          Fetch Data
        </button>
      </div>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <pre
        style={{
          marginTop: '20px',
          padding: '10px',
          border: '1px solid #ccc',
          borderRadius: '5px',
          backgroundColor: '#f8f8f8',
          overflowX: 'auto',
        }}
      >
        {data ? JSON.stringify(data, null, 2) : 'No data to display.'}
      </pre>
    </div>
  );
}

export default MutualFundData;
