import React, { useState } from 'react';
import axios from 'axios';

function StockData() {
    const [symbol, setSymbol] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');

    // Function to fetch data from the backend
    const fetchData = async (stockSymbol) => {
        try {
            const response = await axios.get(`http://localhost:5000/stock/${stockSymbol}`);
            setData(response.data);
            setError('');
        } catch (err) {
            setError('Failed to fetch data. Please try again.');
            console.error('Error:', err.message);
        }
    };

    // Handle the search
    const handleSearch = () => {
        if (!symbol) {
            setError('Please enter a stock symbol.');
            return;
        }
        fetchData(symbol);
    };

    return (
        <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
            <h1>Stock Data Viewer</h1>
            <div>
                <input
                    type="text"
                    placeholder="Enter Stock Symbol"
                    value={symbol}
                    onChange={(e) => setSymbol(e.target.value)}
                    style={{
                        padding: '10px',
                        marginRight: '10px',
                        width: '300px',
                        borderRadius: '5px',
                        border: '1px solid #ccc',
                    }}
                />
                <button
                    onClick={handleSearch}
                    style={{
                        padding: '10px 20px',
                        borderRadius: '5px',
                        border: 'none',
                        backgroundColor: '#007BFF',
                        color: 'white',
                        cursor: 'pointer',
                    }}
                >
                    Search
                </button>
            </div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {data && (
                <div style={{ marginTop: '20px' }}>
                    <h2>Stock Data for {symbol.toUpperCase()}</h2>
                    <p><strong>Current Price:</strong> ${data.c}</p>
                    <p><strong>High Price:</strong> ${data.h}</p>
                    <p><strong>Low Price:</strong> ${data.l}</p>
                    <p><strong>Previous Close:</strong> ${data.pc}</p>
                </div>
            )}
        </div>
    );
}

export default StockData;
