import React, { useState, useEffect } from 'react';
import axios from 'axios';

function StockData() {
    const [symbol, setSymbol] = useState('');
    const [data, setData] = useState(null);
    const [error, setError] = useState('');

    const fetchData = async (stockSymbol) => {
        try {
            const response = await axios.get(`http://localhost:5000/stock/${stockSymbol}`);
            setData(response.data);
            setError('');
        } catch (err) {
            setError('Failed to fetch data. Please try again.');
            console.error('Error:', err.message);
        }
    };

    const handleSearch = () => {
        if (!symbol) {
            setError('Please enter a stock symbol.');
            return;
        }
        fetchData(symbol);
    };

    return (
        <div style={{ padding: '20px' }}>
            <h1>Stock Data Viewer</h1>
            <div>
                <input
                    type="text"
                    placeholder="Enter Stock Symbol"
                    value={symbol}
                    onChange={(e) => setSymbol(e.target.value)}
                    style={{ padding: '10px', marginRight: '10px', width: '300px' }}
                />
                <button onClick={handleSearch} style={{ padding: '10px 20px' }}>
                    Search
                </button>
            </div>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {data && (
                <div style={{ marginTop: '20px' }}>
                    <h2>Stock Data for {symbol.toUpperCase()}</h2>
                    <p><strong>Current Price:</strong> ${data.c}</p>
                    <p><strong>High Price:</strong> ${data.h}</p>
                    <p><strong>Low Price:</strong> ${data.l}</p>
                    <p><strong>Previous Close:</strong> ${data.pc}</p>
                </div>
            )}
        </div>
    );
}

export default StockData;
